import React from "react";
import ChatBot from 'react-simple-chatbot';
import { Segment } from 'semantic-ui-react';
import { ThemeProvider } from 'styled-components';



// Connecting with AI Model
function response(userInput){

    // Accessing the Model through python file
    const { spawn } = require('child_process');
    const AIModelOutput = spawn('python',['my-app/APIExampleChatStream.py',userInput]);

    // AI Model Output
    AIModelOutput.stdout.on('data',(data) =>{
      console.log(data);
    });

    //Error Handling
    AIModelOutput.stderr.on('data', (data) =>{
      console.log(data);
    });

    //Lost Connection with server
    AIModelOutput.on('data', (code) =>{
      console.log(`Lost Connection, please try again later!!! ${code}`)
    }); 

}



// React-ChatBot-UI
export default function ChatbotApp(){

  var message_limit = 25; //user can only send max 25 messages
  var count = 1;
  var steps = new Array(2*message_limit+1);
  var messageArray = new Array(2*message_limit+1);



  steps[0] = {
    id:'Greet',
    message:'Hello, Welcome to our platform. How Can I help you?',
    trigger:String(count),
  }

  // Caching the steps
  messageArray[0] = steps[0];

  while(count<2*message_limit+1){

    steps[count] = {
      id:String(count),
      user:true,
      trigger:String(count+1),
    };

    steps[count+1] = {
      id:String(count+1),
      message: 'Hi I am bot',
      trigger:String(count+2),
    };

    // Caching the steps
    messageArray[0] = steps[count];
    messageArray[0] = steps[count+1];

    // var Message = steps[count+1].message = response('{previousValue}');
    count +=2;

    if(count===2*message_limit+1){
      steps[count] = {
        id:String(count),
        message:'Thank You for chatting with us. Your conversation has ended.',
        end:true
      };
    };

  }
  localStorage.setItem("messageArray", JSON.stringify(messageArray));
  console.log(messageArray);
  // steps[0].message = 'Hi'
  // steps[3].message = '{previousValue} ';
  // // console.log(steps[3].message);


// All possible custom themes 
  const theme = {
    background: '#f5f8fb',
    fontFamily: 'Helvetica Neue',
    headerBgColor: '#e35869',
    headerFontColor: '#fff',
    headerFontSize: '15px',
    botBubbleColor: '#e35869',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a',
  };


// all available config props
  const config ={
    width: "400px",
    height: "500px",
    floating: true,
  };

  return ( 
  <>
    <Segment >
      <ThemeProvider theme={theme}>
        <ChatBot 
        steps={steps}
        {...config}
        />
      </ThemeProvider>
    </Segment>
  </>
);
}
